import { useState } from "react";
import { useLogin } from "../hooks/useLogin";
// import "../CSS/Login.css";
import Loginlottie from "../lotties/Loginlottie";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { login, error, isLoading } = useLogin();

  const handleSubmit = async (e) => {
    e.preventDefault();

    await login(email, password);
  };

  return (
    <div style={{display:"flex",justifyContent:"space-around"}}>
      <Loginlottie />
      <form className="mb-[50px] mt-0 w-96 justify-around rounded shadow-lg " onSubmit={handleSubmit}>
        <h3 className=" py-7 text-center font-bold text-blue-950 text-2xl">Log In</h3>

        <label className="block;
    mt- [20px]
    font-bold">Email address:</label>
        <input
          type="email"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
        />
        <label>Password:</label>
        <input
          type="password"
          onChange={(e) => setPassword(e.target.value)}
          value={password}
        />

        <button disabled={isLoading}>Log in</button>
        {error && <div className="error">{error}</div>}
      </form>
    </div>
  );
};

export default Login;
