const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { login, error, isLoading } = useLogin();

  const handleSubmit = async (e) => {
    e.preventDefault();
    await login(email, password);
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="w-1/3 bg-white p-8 rounded-md shadow-md">
        <Loginlottie />

        <form className="mt-6" onSubmit={handleSubmit}>
          <h3 className="text-2xl font-bold text-center text-gray-700 mb-4">
            Log In
          </h3>

          <label className="block font-bold mt-4">Email address:</label>
          <input
            className="w-full px-3 py-2 border rounded-md focus:outline-none focus:border-blue-500"
            type="email"
            onChange={(e) => setEmail(e.target.value)}
            value={email}
            required
          />

          <label className="block font-bold mt-4">Password:</label>
          <input
            className="w-full px-3 py-2 border rounded-md focus:outline-none focus:border-blue-500"
            type="password"
            onChange={(e) => setPassword(e.target.value)}
            value={password}
            required
          />

          <button
            className={`w-full py-2 bg-blue-500 text-white rounded-md ${
              isLoading ? "opacity-50 cursor-not-allowed" : "hover:bg-blue-600"
            }`}
            type="submit"
            disabled={isLoading}
          >
            {isLoading ? "Logging in..." : "Log in"}
          </button>

          {error && <div className="text-red-500 mt-4 text-center">{error}</div>}
        </form>
      </div>
    </div>
  );
};

export default Login;
